                    $this->Report_model->update_report($id, $params);
                    redirect('task/get_detail/' . $data['report']['task_id']);
                } else {
                    $this->load->model('Task_model');
                    $data['all_task'] = $this->Task_model->get_all_task();

                    $this->load->model('Status_model');
