    /*
     * Deleting report
     */
    function remove($id)
    {
        if (!$this->ion_auth->is_admin()) {
            // redirect them to the login page
            redirect('/', 'refresh');
        } else {

            $report = $this->Report_model->get_report($id);

            // check if the report exists before trying to delete it
            if (isset($report['id'])) {
                $this->Report_model->delete_report($id);
                redirect('report/index');
            } else {
                show_error('The report you are trying to delete does not exist.');
            }
        }
    }
